pragma solidity ^0.4.0;

import "../node_modules/everdragons-shared/solidity/libs/Pausable.sol";
import "../node_modules/everdragons-shared/solidity/libs/DragonItemsContainer.sol";
import "../node_modules/everdragons-shared/solidity/libs/SafeMath.sol";
import "../node_modules/everdragons-shared/solidity/libs/HasAdmin.sol";

/**
 * @title EverDragonsVoucher
 * @dev contract generates and stores voucher code to allow users to buy EverDraong items with
 * discount. Voucher can only be created if the user already bought an item like an egg or a dragon. If the voucher code
 * is used the owner of the voucher will get a reward. Ever user can only own one voucher code
 */
contract EverDragonsVoucher is Pausable, HasAdmin {

    using SafeMath for uint256;

    event VoucherCreated(uint code, address user);

    DragonItemsContainer[] internal dragonItemsContracts;

    uint256 numVouchers = 0;
    mapping(uint256 => address) internal voucherCodeToUser;
    mapping(address => uint256) internal userToVoucherCode;

    modifier hasNoVoucher(address user) {
        require(userToVoucherCode[user] == 0);
        _;
    }

    function _storeNewVoucherCode(address user) private returns (uint) {
        numVouchers = numVouchers.add(1);
        voucherCodeToUser[numVouchers] = user;
        userToVoucherCode[user] = numVouchers;
        emit VoucherCreated(numVouchers, user);
        return numVouchers;
    }

    /////////////////////////////////////////////////////////
    // admin functions

    /**
     * @dev As an admin we can create voucher codes
     */
    function createVoucherForUser(address user) public hasNoVoucher(user) onlyAdmin returns (uint){
        return _storeNewVoucherCode(user);
    }

    /////////////////////////////////////////////////////////
    // owner functions

    /**
     * @dev DragonItemsContracts are contracts on which the user can buy EverDragon items like eggs or dragons.
     */
    function addDragonItemsContainer(address dragonItemsContract) public onlyOwner {
        dragonItemsContracts.push(DragonItemsContainer(dragonItemsContract));
    }

    /**
     * @dev payout ether to owner
     */
    function payout() public onlyAdminLevel {
        owner.transfer(address(this).balance);
    }

    /////////////////////////////////////////////////////////
    // public functions

    /**
     * @dev creates a new voucher code if the user already bought an Ever Dragons item.
     * returns A new voucherCode. The lower byte of the code is a random number. The higher bytes are an increasing
     * number to guarantee the uniqueness of the code.
    */
    function createVoucher() public hasNoVoucher(msg.sender) whenNotPaused returns (uint) {

        // check if the user owns an item
        uint idx = 0;
        for(; idx < dragonItemsContracts.length; idx++ ) {
            if(dragonItemsContracts[idx].isActive() && dragonItemsContracts[idx].hasItem(msg.sender) ) {
                break;
            }
        }

        require(idx != dragonItemsContracts.length);
        return _storeNewVoucherCode(msg.sender);
    }

    /**
     * @dev returns number of created vouchers
     */
    function getNumVouchers() public view returns(uint) {
        return numVouchers;
    }

    /**
     * @dev returns the owner of a voucher
     */
    function getUserForVoucherCode(uint voucherCode) public view returns (address) {
        return voucherCodeToUser[voucherCode];
    }

    /**
     * @dev returns the voucher code for a specific user
     */
    function getVoucherCodeForUser(address user) public view returns (uint) {
        return userToVoucherCode[user];
    }

    /**
     * @dev reject all payments on this contract
     */
    function() payable public {
        revert();
    }

}

