pragma solidity >=0.4.0 <0.6.0;

import "../node_modules/everdragons-shared/solidity/libs/HasRobot.sol";
import "../node_modules/everdragons-shared/solidity/libs/HasWallet.sol";
import "../node_modules/everdragons-shared/solidity/libs/HasPlayerContract.sol";
import "../node_modules/everdragons-shared/solidity/libs/HasDragonContract.sol";
import "../node_modules/everdragons-shared/solidity/libs/Destructible.sol";
import "../node_modules/everdragons-shared/solidity/libs/Pausable.sol";
import "../node_modules/everdragons-shared/solidity/libs/SafeMath.sol";

/// @title Clicker mine game contract
contract Goldmine is Pausable, Destructible, HasDragonContract, HasPlayerContract, HasAdmin {

    using SafeMath for uint;
    uint32 public constant gameID = 2001;

    // State variables
    address public currentGuardian;
    uint public currentGuardianTokenId;
    uint public guardianDuration;
    uint public guardianSince;
    uint public prizePot;

    // Events
    /*
    event StartGame(address player, uint startTime);
    event EndGame(address player, uint totalDuration);
    event ClickMineCart(address player, uint gold, uint diamond);
    event ClickDiamond(address player, uint gold, uint diamond);
    event BuyMiner(address player, MinerType minerType, uint gold);
    event SellMiner(address player, MinerType minerType, uint gold);
    */
    // Modifiers
    modifier gameActive() {
        //Guardian upgraded 4 times
        require(!games[msg.sender].hasFinished, "Game has already ended.");
        require(games[msg.sender].hasStarted, "No game exists for this user");
        _;
    }

    // Enums
    enum MinerType {
        Mole,
        Badger,
        Wyrm,
        Goblin,
        Orc,
        Guardian
    }

    enum StandardUpgradeType {
        Teeth,
        Smoke,
        Fire,
        Roar,
        Fury
    }

    enum MinerUpgradeType {
        Coffee,
        Powerbar,
        Nailfile,
        Pickaxe,
        Dynamite
    }

    enum DiamondUpgradeType {
        Sense,
        Touch,
        Sight,
        Luck,
        Guardian
    }

    // Structs
    struct Miner {
        uint16 count;
        uint32 currentCost;
        uint16 nuggetsPerSecond;
        uint8 currentDiamondsCost;
    }

    struct GameData {

        bool hasStarted;
        bool hasFinished;
        uint totalGold;
        uint totalDiamonds;
        uint goldNuggetsPerSecond;
        uint goldNuggetsPerClick;

        // Timestamp variables
        uint startTime;
        uint endTime;
        uint lastUpdated;

        // Upgrade Arrays
        bool[7] hasStandardUpgrade;
        bool[5] hasMinerUpgrade;
        bool[5] hasDiamondUpgrade;

        // Mappings
        mapping(uint8 => Miner) miners;

        // More variables
        uint goldNuggetsBoost;
        uint tokenId;
        bool hasWon;
    }

    mapping(address => GameData) public games;

    function addToPrizePot(uint amount) public onlyAdmin {
        prizePot += amount;
    }

    /// @dev Create a new game for the player
    function newGame(uint tokenId) public {

        require(!games[msg.sender].hasStarted || games[msg.sender].hasFinished, "player already started game");

        uint32 state = dragonContract.getDragonState(tokenId);
        require(state == 0, "dragon is already in a game");

        address user = dragonContract.ownerOf(tokenId);
        require(msg.sender == user, "not owner of token");

        uint16 fullness;
        (,,,fullness,,) = dragonContract.getDragon(tokenId);

        require(fullness >= 30, "not enough fullness");
        dragonContract.setDragonState(tokenId, gameID);
        dragonContract.modifyFullness(tokenId, int16(- 30), 0);

        prizePot += 24;
        if(currentGuardian != address(0)) {
            playerContract.modifyItem(currentGuardian, "goldToClaim", 3);
        }

        games[msg.sender] = GameData(
            true, //hasStarted
            false, //hasFinished
            0, //totalGold
            0, //totalDiamonds
            0, //goldNuggetsPerSecond
            10, //goldNuggetsPerClick
            now, //startTime
            0, //endTime
            now, //lastUpdated
            [false, false, false, false, false, false, false], //hasStandardUpgrade
            [false, false, false, false, false], //hasMinerUpgrade
            [false, false, false, false, false], //hasDiamondUpgrade
            100, //goldNuggetsBoost
            tokenId,
            false
        );

        // Mole. A little mole.
        // Produces 1 goldNugget every 10 seconds.
        games[msg.sender].miners[0] = Miner(0, 15, 1, 0);
        // Badger. A wild badger with sharp claws.
        // Produces 1 goldNugget every 2 seconds.
        games[msg.sender].miners[1] = Miner(0, 100, 5, 0);
        // Wyrm. Rock wyrms love the mines.
        // Produces 5 goldNuggets per second.
        games[msg.sender].miners[2] = Miner(0, 1250, 50, 0);
        // Goblin. Goblins are excellent mine workers.
        // Produces 17 goldNuggets per second.
        games[msg.sender].miners[3] = Miner(0, 5000, 170, 0);
        // Orc. Orcs are strong and too dumb to steal diamonds when they find one.
        // Produces 52 goldNuggets per second.
        games[msg.sender].miners[4] = Miner(0, 30000, 520, 0);
        // Guardian. Once completed you don't need to oversee it anymore.
        // Produces 500 gold nuggets per second.
        games[msg.sender].miners[5] = Miner(0, 200000, 5000, 25);

        // emit StartGame(msg.sender, now);
    }

    function getMiners() public view returns (uint[6] count, uint[6] currentCost, uint[6] nuggetsPerSecond) {
        for(uint8 i = 0; i < 6; i++) {
            count[i] = games[msg.sender].miners[i].count;
            currentCost[i] = games[msg.sender].miners[i].currentCost;
            nuggetsPerSecond[i] = games[msg.sender].miners[i].nuggetsPerSecond;
        }
    }

    function getUpgrades() public view returns (bool[7] standard, bool[5] miner, bool[5] diamond) {
        return (games[msg.sender].hasStandardUpgrade, games[msg.sender].hasMinerUpgrade, games[msg.sender].hasDiamondUpgrade);
    }

    /// @dev Produces gold and diamond when clicking the mine cart
    function clickMineCart() public gameActive {

        GameData storage game = games[msg.sender];
        game.totalGold = game.totalGold.add(game.goldNuggetsPerClick);

        uint rnd = random(100);
        //diamond demand
        if (rnd <= 10 ||
            (game.hasDiamondUpgrade[0] && rnd <= 20) ||
            (game.hasDiamondUpgrade[1] && rnd <= 30)) {
            game.totalDiamonds = game.totalDiamonds.add(1);
        }

        updateTotalGoldNuggets(game);
        // emit ClickMineCart(msg.sender, game.goldNuggetsPerClick, diamonds);
    }


    /// @dev Logic related to clicking diamond
    function clickDiamond() public gameActive{

        GameData storage game = games[msg.sender];

        uint luckyNumber = now %100;

        // Has Sight
        if (game.hasDiamondUpgrade[2]){
            require(luckyNumber <= 15, "No lucky diamond found.");
        }
        else {
            require(luckyNumber <= 10, "No lucky diamond found.");
        }

        // Has Luck
        if (game.hasDiamondUpgrade[3]){
            game.totalDiamonds = game.totalDiamonds.add(10);
        } else {
            game.totalDiamonds = game.totalDiamonds.add(2);
        }

        updateTotalGoldNuggets(game);
        // emit ClickDiamond(msg.sender, goldAmount, diamondAmount);
    }

    /// @return Duration since last updated in seconds
    function getDurationSinceLastUpdate(uint lastUpdated) public view returns (uint) {
        return now.sub(lastUpdated);
    }

    /// @return Total duration of game in secords
    function getTotalDuration(uint startTime) public view returns (uint) {
        return now.sub(startTime);
    }

    ///@dev current guardian's time + 1 minute every 4 hours
    function getTimeToBeat() public view returns (uint) {
        return guardianDuration + ((now - guardianSince) / 240);
    }

    function getTotalNuggets() public view returns (uint, uint64) {
        GameData storage game = games[msg.sender];
        uint256 additionalNuggets = game.goldNuggetsPerSecond
        .mul(getDurationSinceLastUpdate(game.lastUpdated)) / 10;

        return (game.totalGold + additionalNuggets, uint64(now));
    }
    /// @dev Buys a miner for current cost
    /// @param minerType one of the 6 miner types
    function buyMiner(MinerType minerType) public gameActive {

        GameData storage game = games[msg.sender];
        updateTotalGoldNuggets(game);

        require(uint8(minerType) <= 5, "Invalid miner type.");
        Miner storage miner = game.miners[uint8(minerType)];

        if (minerType == MinerType.Guardian) {

            uint costOfDiamondsMultiplier = uint(miner.count) + 1;
            uint costOfDiamonds = costOfDiamondsMultiplier * 5;
            miner.currentDiamondsCost = uint8(costOfDiamonds);
            require(game.hasDiamondUpgrade[4], "Needs Guardian upgrade to buy Guardian.");
            require(game.totalDiamonds >= costOfDiamonds, "Needs more diamonds.");
            game.totalDiamonds = game.totalDiamonds.sub(costOfDiamonds);
        }

        require(game.totalGold >= miner.currentCost,
            "Player can't afford this miner."
        );

        game.totalGold -= miner.currentCost;
        game.goldNuggetsPerSecond += miner.nuggetsPerSecond;

        miner.count++;
        miner.currentCost = miner.currentCost * 115 / 100;

        // emit BuyMiner(msg.sender, minerType, miner.currentCost);

        if (minerType == MinerType.Guardian && miner.count >= 4) {

            endGame();
        }
    }


    /// @dev ends the game and replaces guardian if time is beaten
    function endGame() private  {

        GameData storage game = games[msg.sender];

        uint totalDuration = getTotalDuration(game.startTime);
        uint timeToBeat = getTimeToBeat();

        if (totalDuration < timeToBeat) {
            guardianDuration = totalDuration;
            currentGuardian = msg.sender;
            currentGuardianTokenId = game.tokenId;
            guardianSince = now;
            game.hasWon = true;
            uint win = prizePot / 2;
            prizePot -= win;
            playerContract.modifyItem(msg.sender, "goldToClaim", int32(win));
            dragonContract.modifyDragonPrestige(game.tokenId, 1);
            dragonContract.modifyDragonExperience(game.tokenId, 1);
        } else {
            prizePot -= 10;
            playerContract.modifyItem(msg.sender, "goldToClaim", 10);
            dragonContract.modifyDragonExperience(game.tokenId, 1);
        }

        game.endTime = now;
        quit();
        // emit EndGame(msg.sender, totalDuration);
    }

    function quit() public gameActive {
        GameData storage game = games[msg.sender];

        dragonContract.setDragonState(game.tokenId, 0);
        game.hasFinished = true;

    }

    /// @dev Sells a miner for 50% of current cost
    /// @param minerType one of the 6 miner types
    function sellMiner(MinerType minerType) public gameActive {

        GameData storage game = games[msg.sender];

        require(uint8(minerType) < 5, "Invalid miner type.");
        Miner storage miner = game.miners[uint8(minerType)];
        require(miner.count > 0, "No miners to sell.");

        uint sellPrice = miner.currentCost / 2;
        game.totalGold = game.totalGold.add(sellPrice);
        game.goldNuggetsPerSecond -= miner.nuggetsPerSecond;

        miner.count -= 1;
        // emit SellMiner(msg.sender, minerType, sellPrice);

        updateTotalGoldNuggets(game);
    }


    /// @dev Buys an upgrade to the clicker
    /// @param upgradeType one of the 5 standard upgrade types
    function buyStandardUpgrade(StandardUpgradeType upgradeType)
    public
    gameActive
    {
        // Teeth. Gets you +10 goldNugget/click
        if (upgradeType == StandardUpgradeType.Teeth) {
            upgradeStandard(uint8(upgradeType), 50, 10, 0, 0);
        }
        // Smoke. Gets you +15 goldNugget/click
        else if (upgradeType == StandardUpgradeType.Smoke) {
            upgradeStandard(uint8(upgradeType), 200, 15, 0, 0);
        }
        // Fire. Gets you.+25 goldNugget/click
        else if (upgradeType == StandardUpgradeType.Fire) {
            upgradeStandard(uint8(upgradeType), 700, 25, 0, 0);
        }
        // Roar. Gets you goldNugget/click x2. goldNugget production +5%
        else if (upgradeType == StandardUpgradeType.Roar) {
            upgradeStandard(uint8(upgradeType), 4000, 0, 20, 5);
        }
        // Fury. Gets you goldNugget/click x2.5. goldNugget production +10%
        else if (upgradeType == StandardUpgradeType.Fury) {
            upgradeStandard(uint8(upgradeType), 30000, 0, 25, 10);
        }
        else
            revert("Invalid upgrade type.");
    }



    /// @dev Buys an upgrade that is applied to miners
    /// @param upgradeType one of the 5 miner upgrade types
    function buyMinerUpgrade(MinerUpgradeType upgradeType)
    public
    gameActive
    {
        require(uint8(upgradeType) <= 4, "Invalid upgrade type.");
        GameData storage game = games[msg.sender];
        // Coffee can dig faster.
        // mole gathering x2. badger gathering x2. rock wyrm gathering x2
        if (upgradeType == MinerUpgradeType.Coffee) {
            require(
                game.miners[0].count >= 10 &&
                game.miners[1].count >= 7 &&
                game.miners[2].count >= 4,
                "10 moles, 7 badgers and 4 wyrms are required."
            );
            upgradeMiner(uint8(upgradeType), 10000, 0, [2, 2, 2, 1, 1]);
            updateGoldNuggetsPerSecond(game);
        }
        // Everyone works better with a break!
        // mole gathering x2. badger gathering x2. rock wyrm gathering x2
        else if (upgradeType == MinerUpgradeType.Powerbar) {
            require(
                game.miners[0].count >= 15 &&
                game.miners[1].count >= 12 &&
                game.miners[2].count >= 8,
                "15 moles, 12 badgers and 8 wyrms are required."
            );
            upgradeMiner(uint8(upgradeType), 20000, 0, [2, 2, 2, 1, 1]);
            updateGoldNuggetsPerSecond(game);
        }
        // Oh boy this stuff makes your fit!
        // mole gathering x2. badger gathering x2. rock wyrm gathering x2
        else if (upgradeType == MinerUpgradeType.Nailfile) {
            require(
                game.miners[0].count >= 20 &&
                game.miners[1].count >= 16 &&
                game.miners[2].count >= 12,
                "20 moles, 16 badgers and 12 wyrms are required."
            );
            upgradeMiner(uint8(upgradeType), 50000, 1, [2, 2, 2, 1, 1]);
            updateGoldNuggetsPerSecond(game);
        }
        // Sharper, better, faster! goblin production x2. cave orc production x2
        else if (upgradeType == MinerUpgradeType.Pickaxe) {
            require(
                game.miners[3].count >= 10 &&
                game.miners[4].count >= 1,
                "10 goblins and 1 orc is required."
            );
            upgradeMiner(uint8(upgradeType), 50000, 1, [1, 1, 1, 2, 2]);
            updateGoldNuggetsPerSecond(game);
        }
        // BOOM! goblin production x2. cave orc production x3
        else if (upgradeType == MinerUpgradeType.Dynamite) {
            require(
                game.miners[3].count >= 10 &&
                game.miners[4].count >= 5,
                "15 goblins and 3 orcs are required."
            );
            upgradeMiner(uint8(upgradeType), 100000, 5, [1, 1, 1, 2, 3]);
            updateGoldNuggetsPerSecond(game);
        }
    }

    /// @dev Buys an upgrade to diamond frequency (or Guardian)
    /// @param upgradeType one of the 5 diamond upgrade types
    function buyDiamondUpgrade(DiamondUpgradeType upgradeType)
    public
    gameActive
    {
        GameData storage game = games[msg.sender];

        // 20% chance of gaining 1 diamond per goldNugget click
        if (upgradeType == DiamondUpgradeType.Sense) {
            upgradeDiamond(uint8(upgradeType), 1, 0);
        }
        // 30% chance of gaining 1 diamond per goldNugget click
        else if (upgradeType == DiamondUpgradeType.Touch) {
            upgradeDiamond(uint8(upgradeType), 2, 0);
        }
        // lucky diamonds appear 50% longer often
        else if (upgradeType == DiamondUpgradeType.Sight) {
            upgradeDiamond(uint8(upgradeType), 2, 0);
        }
        // This unlocks the Guardian option.
        else if (upgradeType == DiamondUpgradeType.Guardian) {
            require(
                game.miners[3].count >= 10 &&
                game.miners[4].count >= 3,
                "Requires at least 10 goblins and 3 orcs."
            );
            upgradeDiamond(uint8(upgradeType), 5, 0);
        }
        // Lucky diamond effects are 5 times more powerful
        else if (upgradeType == DiamondUpgradeType.Luck) {
            upgradeDiamond(uint8(upgradeType), 0, 100000);
        }
        else
            revert("Invalid upgrade type.");
    }


    /// @dev Applies upgrade to clicker and multipliers
    /// @param upgradeIndex Index related to StandardUpgradeType enum
    /// @param goldCost Cost of upgrade in gold nuggets
    /// @param nuggetsPerClick Number of nuggets to add per click
    /// @param nuggetsPerClickMultiplier Factor to multiply current nuggets per click
    /// @param nuggetsPerSecondMultiplier Factor to multiple current nuggets per sec
    function upgradeStandard(
        uint8 upgradeIndex,
        uint goldCost,
        uint nuggetsPerClick,
        uint nuggetsPerClickMultiplier,
        uint nuggetsPerSecondMultiplier
    )
    private
    gameActive
    {

        GameData storage game = games[msg.sender];
        updateTotalGoldNuggets(game);

        require(!game.hasStandardUpgrade[upgradeIndex], "Upgrade already owned.");
        require(game.totalGold >= goldCost, "More gold is required.");

        game.goldNuggetsPerClick = game.goldNuggetsPerClick.add(nuggetsPerClick);

        if (nuggetsPerClickMultiplier != 0) {
            game.goldNuggetsPerClick = game.goldNuggetsPerClick.mul(nuggetsPerClickMultiplier) / 10;
        }
        if (nuggetsPerSecondMultiplier != 0) {
            game.goldNuggetsBoost = game.goldNuggetsBoost.add(nuggetsPerSecondMultiplier);
        }

        game.totalGold = game.totalGold.sub(goldCost);
        game.hasStandardUpgrade[upgradeIndex] = true;

        updateGoldNuggetsPerSecond(game);
    }


    /// @dev Applies upgrade to related miners
    /// @param upgradeIndex Index related to MinerUpgradeType enum
    /// @param goldCost Cost of upgrade in gold nuggets
    /// @param diamondCost Cost of upgrade in diamonds
    /// @param minerMultipliers multiplier factor for each miner
    function upgradeMiner(
        uint8 upgradeIndex,
        uint goldCost,
        uint diamondCost,
        uint8[5] memory minerMultipliers
    )
    private
    gameActive
    {
        GameData storage game = games[msg.sender];
        updateTotalGoldNuggets(game);

        require(!game.hasMinerUpgrade[upgradeIndex], "Upgrade already owned.");
        require(game.totalGold >= goldCost, "More gold is required.");
        require(game.totalDiamonds >= diamondCost, "More diamonds are required.");

        for (uint8 i = 0; i <= 4; i++) {
            game.miners[i].nuggetsPerSecond = game.miners[i].nuggetsPerSecond * minerMultipliers[i];
        }

        game.totalGold = game.totalGold.sub(goldCost);
        game.totalDiamonds = game.totalDiamonds.sub(diamondCost);
        game.hasMinerUpgrade[upgradeIndex] = true;
    }


    /// @dev diamond upgrade flags are used in mine and diamond click functions
    /// @dev Applies upgrade to related miners
    /// @param upgradeIndex Index related to DiamondUpgradeType enum
    /// @param diamondCost Cost of upgrade in diamonds
    /// @param goldCost Cost of upgrade in gold nuggets
    function upgradeDiamond(
        uint8 upgradeIndex,
        uint diamondCost,
        uint goldCost
    )
    private
    gameActive
    {
        GameData storage game = games[msg.sender];
        updateTotalGoldNuggets(game);

        require(!game.hasDiamondUpgrade[upgradeIndex], "Upgrade already owned.");
        require(game.totalDiamonds >= diamondCost, "More diamonds are required.");
        require(game.totalGold >= goldCost, "More gold is required.");

        game.hasDiamondUpgrade[upgradeIndex] = true;
        game.totalDiamonds = game.totalDiamonds.sub(diamondCost);
        game.totalGold = game.totalGold.sub(goldCost);
    }


    /// @dev adds mined nuggets since last update to total
    function updateTotalGoldNuggets(GameData storage game) private gameActive
    {
        uint256 additionalNuggets = game.goldNuggetsPerSecond
        .mul(getDurationSinceLastUpdate(game.lastUpdated)) / 10;

        game.totalGold = game.totalGold.add(additionalNuggets);
        game.lastUpdated = now;
    }

    /// @dev recalculates nuggets per second
    function updateGoldNuggetsPerSecond(GameData storage game) private gameActive
    {

        uint256 nuggetsPerSecondThisOne = 0;
        uint256 nuggetsPerSecondCounter = 0;

        for (uint8 i = 0; i <= 5; i++)
        {
            nuggetsPerSecondThisOne = game.miners[i].nuggetsPerSecond * game.miners[i].count;
            nuggetsPerSecondCounter = nuggetsPerSecondCounter.add(nuggetsPerSecondThisOne);
        }
        game.goldNuggetsPerSecond = nuggetsPerSecondCounter.mul(game.goldNuggetsBoost) / 100;
    }

    /// @return Random number less than specified value
    function random(uint value) private view returns (uint) {
        return uint(keccak256(abi.encodePacked(block.timestamp, block.difficulty))) % value;
    }

}

