pragma solidity ^0.4.0;

import "./RaceBetting.sol";

contract Race is RaceBetting {

    constructor(address registrarAddress) public EDGBase(2001) HasRegistrar(registrarAddress) {}

    function getGameId() public view returns (uint gameId) {
        return playerToGameId[msg.sender];
    }

    function getGameForUser()
    public
    view
    returns (uint gameId, uint32 price, uint16 maxPlayers, uint64[] dragons, uint64[] winners, uint8 status)
    {
        gameId = playerToGameId[msg.sender];
        (price, maxPlayers, dragons, winners, status) = getGame(gameId);
    }

    function getVendorGame()
    public
    view
    returns (uint gameId, uint32 price, uint16 maxPlayers, uint64[] dragons, uint64[] winners, uint8 status)
    {
        gameId = playerToGameId[msg.sender];
        (price, maxPlayers, dragons, winners, status) = getGame(gameId);
    }
}
